import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useStripe, useElements, CardExpiryElement, CardCvcElement, CardNumberElement } from '@stripe/react-stripe-js';
import '/Users/casha/Desktop/pistola/pistola/client/src/styles/CheckoutForm.css'

const CheckoutForm: React.FC = () => {
  const location = useLocation();
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const { total, email, clientSecret } = location.state || {};

  const [name, setName] = useState<string>('');
  const [address, setAddress] = useState<string>('');
  const [city, setCity] = useState<string>('');

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    const cardNumberElement = elements.getElement(CardNumberElement);

    const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: cardNumberElement!,
        billing_details: {
          name,
          email,
          address: {
            line1: address,
          },
          city: {
            line2: city,
          }
        },
      },
    });

    if (error) {
      console.error(error);
    } else if (paymentIntent && paymentIntent.status === 'succeeded') {
      navigate('/success');
    }
  };

  return (
        <form onSubmit={handleSubmit}>
          <div>
            <label>
              Name
              <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              />
            </label>
          </div>
          <div>
            <label>
              Address
              <input
              type="text"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              required
            />
            </label>
          </div>
          <div>
            <label>
              Card Number
              <CardNumberElement />
            </label>
          </div>
          <div>
            <label>
              Expiry Date 
              <CardExpiryElement />
            </label>
          </div>
          <div>
            <label>
              CVC
              <CardCvcElement />
            </label>
          </div>
          <button type="submit" disabled={!stripe || !elements}>
            Pay ${total ? total.toFixed(2) : '0.00'} CAD
          </button>
        </form>
      );
    };

export default CheckoutForm;
